hamming_distance <- function(dna1, dna2){
    dna1_split <- strsplit(dna1, "")[[1]]
    dna2_split <- strsplit(dna2, "")[[1]]
    hamm_dist <- 0

    for(i in 1: length(dna1_split)){
        if(dna1_split[i] != dna2_split[i]){
            hamm_dist <- hamm_dist + 1
        }
    }

    return(hamm_dist)
}

str1 <- ""
str2 <- ""
print(hamming_distance(str1, str2))